def kappa_preserving_auc_adjustment(kappa_preds, auc_preds, gt_threshold=.5):
    # For a good kappa model and a good AUC model, come up with a set of predictions that yields
    # the same kappa but matches the good AUC as closely as possible by rearranging positive and
    # negative predictions (positive defined by > gt_threshold)
    kappa_preds, auc_preds = np.array(kappa_preds), np.array(auc_preds)
    pos_auc = auc_preds[kappa_preds > gt_threshold]
    neg_auc = auc_preds[kappa_preds <= gt_threshold]
    pos_auc = (pos_auc - pos_auc.min()) / pos_auc.max() / 3 + .51
    neg_auc = (neg_auc - neg_auc.min()) / neg_auc.max() / 3
    auc_preds[kappa_preds > gt_threshold] = pos_auc
    auc_preds[kappa_preds <= gt_threshold] = neg_auc
    return auc_preds


